#include <iostream>#include <cstdlib>#include <ctime>using namespace std;int main(){	int tries = 0;	int gn = 0;	int hn = 0;	int totaltries = 0;	cout << "Hello, welcome to guess number game" << endl;	cout << "Well, write highest value of guess number for random generation: " << endl;	cin >> gn;	cout << "Now, write tries: " << endl;	cin >> totaltries;	srand(time(0));	gn = rand() % gn + 1;	do {		if (tries >= 3) {			cout << "You already used all tries, try again" << endl;			break;		}		else {			cout << "Choose number: " << endl;			cin >> hn;			if (hn == gn) {				cout << "Success! u found number via " << tries << " try" << endl;				cin >> hn;			}			else if (hn >= 2147483647) {				cout << "No! highest value u can use is 2147483646" << endl;				break;			}			else {				cout << "Wrong" << endl;				tries++;			}		}	} while (hn != gn);	return 0;	// program ended?? huh}